function runTest(evt) {
    grada = document.getElementById("inneruse");
    gradb = document.getElementById("offset");
    offset();
}
var turns = 5;
function offset(){
    grada.setAttribute ("transform", "");
    gradb.setAttribute ("offset", "1.0");
    turns--;
    if (turns > 0)
        window.setTimeout("offset()",10)
}
